import { Component, OnInit } from '@angular/core';
import { ImageService } from './image.service';
import { Observable } from 'rxjs';
import { BlogImage } from '../../models/blog-image.model';

@Component({
  selector: 'app-image-selector',
  templateUrl: './image-selector.component.html',
  styleUrls: ['./image-selector.component.css']
})
export class ImageSelectorComponent implements OnInit {
  private file?: File;
  fileName: string = '';
  title: string = '';
  images$?: Observable<BlogImage[]>;

  constructor(private imageService: ImageService) {

  }
  ngOnInit(): void {
    this.getImages();
  }

  onFileUploadChange(event: Event): void {
    const element = event.currentTarget as HTMLInputElement;
    this.file = element.files?.[0];
  }

  uploadImage(): void {
    if (this.file && this.fileName !== '' && this.title !== '') {
      // Image service to upload the image
      this.imageService.uploadImage(this.file, this.fileName, this.title)
      .subscribe({
        next: (response) => {
          this.getImages();
        }
      });
    }
  }

  private getImages() {
    this.images$ = this.imageService.getAllImages();
  }

}
<div class="card h-100">
  <div class="card-header">
    <h3 class="card-title">Images</h3>
  </div>

  <div class="card-body">
    <div class="d-flex flex-md-row flex-sm-column flex-wrap">
      <div class="col-md-8 p-4" style="overflow-y: scroll;">
        <ng-container *ngIf="images$ | async as images">
          <div class="d-flex flex-wrap">
            <div *ngFor="let image of images" class="me-1 bg-white d-flex align-items-center flex-column border p-3"
              style="min-width: 100px; justify-content: center; cursor: pointer;">
              <img [src]="image.url" [title]="image.title" style="height: 100px;">
              <span class="mt-1">{{image.title}}</span>
            </div>
          </div>
        </ng-container>
      </div>
      <div class="col-md-4 p-4 bg-light">
        <h5>Upload Image</h5>

        <form #form="ngForm" (ngSubmit)="uploadImage()">
          <div class="mt-3">
            <input type="file" class="form-control" (change)="onFileUploadChange($event)">
          </div>

          <div class="mt-3">
            <input type="text" class="form-control" placeholder="File Name" name="filename" [(ngModel)]="fileName">
          </div>

          <div class="mt-3">
            <input type="text" class="form-control" placeholder="Title" name="title" [(ngModel)]="title">
          </div>

          <div class="mt-3">
            <button type="submit" class="btn btn-dark">Upload</button>
          </div>

        </form>
      </div>
    </div>
  </div>
</div>
