import { Component, OnInit } from '@angular/core';
import { CategoryService } from '../services/category.service';
import { Category } from '../models/category.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-category-list',
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.css']
})
export class CategoryListComponent implements OnInit {
  categories$?: Observable<Category[]>;
  totalCount? : number;
  list: number[] = [];
  pageNumber = 1;
  pageSize = 3;

  constructor(private categoryService: CategoryService) {

  }

  ngOnInit(): void {
    this.categoryService.getCategoryCount() 
    .subscribe({
      next: (value) => {
        this.totalCount = value;
        this.list = new Array(Math.ceil(value / this.pageSize))

        this.categories$ = this.categoryService.getAllCategories(
          undefined,
          undefined,
          undefined,
          this.pageNumber,
          this.pageSize
        );
      }
    })
  }

  onSearch(query: string) {
    this.categories$ = this.categoryService.getAllCategories(query);
  }

  sort(sortBy: string, sortDirection: string) {
    this.categories$ = this.categoryService.getAllCategories(undefined, sortBy, sortDirection);
  }

  getPage(pageNumber: number) {
    this.pageNumber = pageNumber;

    this.categories$ = this.categoryService.getAllCategories(
      undefined,
      undefined,
      undefined,
      this.pageNumber,
      this.pageSize
    );
  }

  getNextPage() {
    if (this.pageNumber + 1 > this.list.length) {
      return;
    }


  this.pageNumber += 1;
    this.categories$ = this.categoryService.getAllCategories(
      undefined,
      undefined,
      undefined,
      this.pageNumber,
      this.pageSize
    );
  }

  getPrevPage() {
    if (this.pageNumber - 1 < 1) {
      return;
    }


  this.pageNumber -= 1;
    this.categories$ = this.categoryService.getAllCategories(
      undefined,
      undefined,
      undefined,
      this.pageNumber,
      this.pageSize
    );
  }

}
<div class="container">

    <h1 class="mt-3">Category List</h1>

    <div class="d-flex justify-content-between mt-3">
        <div class="d-flex">
            <input type="text" class="form-control" #queryText>
            <button type="button" class="btn btn-warning ms-3" (click)="onSearch(queryText.value)">Search</button>
        </div>


        <a [routerLink]="['/admin/categories/add']" class="btn btn-primary">Add Category</a>
    </div>

    <ng-container *ngIf="categories$ | async as categories">
        <table class="table table-bordered mt-3">
            <thead>
                <tr>
                    <th>Id</th>
                    <th>
                        Name

                        <span>
                            <span>
                                <button type="button" class="btn btn-light" (click)="sort('name', 'asc')">
                                    <i class="bi bi-arrow-up-short"></i>
                                </button>
                            </span>
                            <span>
                                <button type="button" class="btn btn-light" (click)="sort('name', 'desc')">
                                    <i class="bi bi-arrow-down-short"></i>
                                </button>
                            </span>
                        </span>
                    </th>
                    <th>
                        Url Handle

                        <span>
                            <span>
                                <button type="button" class="btn btn-light" (click)="sort('url', 'asc')">
                                    <i class="bi bi-arrow-up-short"></i>
                                </button>
                            </span>
                            <span>
                                <button type="button" class="btn btn-light" (click)="sort('url', 'desc')">
                                    <i class="bi bi-arrow-down-short"></i>
                                </button>
                            </span>
                        </span>
                    </th>
                    <th> </th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let category of categories">
                    <td>{{category.id}}</td>
                    <td>{{category.name}}</td>
                    <td>{{category.urlHandle}}</td>
                    <td>
                        <a class="button btn-light" [routerLink]="['/admin/categories', category.id]">Edit</a>
                    </td>
                </tr>
            </tbody>
        </table>

        <nav aria-label="Page navigation example">
            <ul class="pagination">
              <li class="page-item"><a class="page-link" (click)="getPreviousPage()">Previous</a></li>

              <li *ngFor="let item of list;let index = index" class="page-item" >
                <a class="page-link" (click)="getPage(index + 1)">{{index + 1}}</a>
            </li>



              <li class="page-item"><a class="page-link" (click)="getNextPage()">Next</a></li>
            </ul>
          </nav>

    </ng-container>

</div>
